mapper.select:
select
  id, username, password, email, created_at
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and created_at = #{createdAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and created_at = #{createdAt}
